// advent of code day one http://adventofcode.com/2017/day/1

const input = ''

// part one

const compare = (f,s) => {

	let add = 0;

	if (f === s) {
		add = s;
	}
	else {
		add = 0;
	}

	return add;
}

const uncaptcha = (input) => {

	let sum = 0;
	let seq = input.split('');
	//let check = 0;

	for (let i = 0; i < seq.length; i++) {

		let add = 0;

		if (i < input.length-1) {
			add = compare(input[i], input[i+1])
		}
		else if (i === input.length-1) {
			add = compare(input[input.length-1], input[0])
		}

		sum = sum + Number(add)
		//check += add
	}

	return sum;
	//return {sum, check};
}

// tests
//uncaptcha('1122') // returns 3
//uncaptcha('1111') // returns 4
//uncaptcha('1234') // returns 0
//uncaptcha('91212129') // returns 9
//uncaptcha(input) // returns 1150

// part two

const uncaptcha2 = (input) => {

	let sum = 0;
	//let check = 0;
	let seq = input.split('');
	
	for (let i = 0; i < seq.length; i++) {
		let add = 0;
		let mid = Math.ceil(seq.length/2)+i
		
		if (mid >= seq.length) {
			mid = mid - seq.length
		}

		add = compare(input[i],input[mid])
		sum = sum + Number(add)
	}
	
	return sum;
}

